import React, { Component } from "react";

class Counter extends Component {
  state = {
    counter: 0,
    tags: ["tag1", "tag2"],
  };

  renderTags() {
    if (this.state.tags.length === 0) return <p>Nothing to Show</p>;
    return (
      <ul>
        {this.state.tags.map((tag) => (
          <li key="{tag}">{tag}</li>
        ))}
      </ul>
    );
  }

  render() {
    return <div>{this.renderTags()}</div>;
  }
}

export default Counter;
